mapper.update:
		update T_TAGGING 
		set ScanNum=ScanNum+1,
		 Modifyer =#{creater},
		 ModifyTime =#{createTime},
		 DetailNo =#{detailNo}
		where
		     OrderNo =#{orderNo}
    	 AND SKUCode =#{skuCode}
    	 AND TaggingAddress =#{taggingAddress}
    	 AND DATE_FORMAT(ScanTime,"%Y%m%d") =DATE_FORMAT(#{scanTime},"%Y%m%d")
